import './App.css';
import React from 'react';
import { BrowserRouter as Router, Route } from 'react-router-dom';
import { Link } from 'react-router-dom';
import HomePage from './pages/HomePage';
import AddExercisePage from './pages/AddExercisePage';
import EditExercisePage from './pages/EditExercisePage';
import { useState } from 'react';
import homePageImg from "./img/weights.jpg"
import addExcerciseImg from "./img/man.jpg"

function App() {
    const [exerciseToEdit, setExerciseToEdit] = useState();
    const[backgroundImg, setBackgroundImg] = useState(homePageImg);
    const[hover,setHover] = useState()


  return (
<div class="bg-image" 
            style={{ backgroundImage: `url(${backgroundImg}`,backgroundRepeat:"no-repeat", backgroundSize: "cover"  }} >
  <div class="container-fluid">
    <div class="row d-flex justify-content-center">
      <div class="col-sm-6 d-flex justify-content-center">
          <div class="card text-white text-center bg-dark mb-3" style={{ maxWidth: 500 }}>
            <div class="card-body">
              <h1 class="card-title"> Exercise Log</h1>
                <p class="card-text">A full stack MERN app that records exercises</p>
              </div>
            </div>
          </div>
        <main>
          <div className="App">
            <Router>
            <div class="row d-flex justify-content-center">
              <div class="col-sm-6 d-flex justify-content-center">
                <div class="card text-white text-center bg-dark mb-3" style={{ maxWidth: 500 }}>
                  <div class="card-body">
                    <h1 class="card-title"><Link style={{textDecoration: 'none',  color: hover ? 'red' : 'green'}}
                     onClick={() => setBackgroundImg(homePageImg)} to="/" onMouseEnter={() => setHover(true)} onMouseLeave={() => setHover(false) }
                     >Homepage</Link></h1>
                 </div>
              </div>
            </div>
            </div>
               <h1><Link to="/add-exercise" onClick={() => setBackgroundImg(addExcerciseImg)}>Add an exercise</Link></h1>
                <div className="App-header">
                 <Route path="/" exact>
                    <HomePage setExerciseToEdit={setExerciseToEdit} setBackgroundImg={setBackgroundImg}/>
                  </Route>
                  <Route path="/add-exercise">
                    <AddExercisePage/>
                  </Route>
                  <Route path="/edit-exercise">
                    <EditExercisePage exerciseToEdit={exerciseToEdit}/>
                  </Route>
                </div>
            </Router>
          </div>
        </main>
      <footer>Â© 2022 Jerrod Lepper</footer>
    </div>
  </div>
</div>
  );
}

export default App;
